class Solution1073 {
    fun addNegabinary(arr1: IntArray, arr2: IntArray): IntArray {
        val n1 = if (arr1.size > arr2.size) {
            arr1
        } else {
            arr2
        }
        val n2 = if (arr1.size > arr2.size) {
            arr2
        } else {
            arr1
        }

        val list = arrayListOf<Int>()
        var add = 0
        for (i in 0 until n1.size) {
            val it1 = n1[n1.lastIndex - i]
            val it2 = if (n2.lastIndex >= i) {
                n2[n2.lastIndex - i]
            } else {
                0
            }

            add = if (it1 + it2 + add == 2) {
                list.add(0)
                -1
            } else if (it1 + it2 + add == 1 || it1 + it2 + add == 0) {
                list.add(it1 + it2 + add)
                0
            } else {
                // -1
                list.add(1)
                if (add == -1) {
                    1
                } else {
                    -1
                }
            }
        }
        if (add == -1) {
            list.add(1)
            list.add(1)
        }

        list.reverse()
        var first = false
        val ans = arrayListOf<Int>()
        for (i in 0 until list.size) {
            if (list[i] == 0 && !first) {
                continue
            } else {
                first = true
                ans.add(list[i])
            }
        }
        return if (ans.isEmpty()) {
            intArrayOf(0)
        } else {
            ans.toIntArray()
        }
    }
